function bdev_remove_site_health_wpmenu() {
    remove_submenu_page('tools.php', 'site-health.php');
}
add_action('admin_menu', 'bdev_remove_site_health_wpmenu');

function bdev_block_site_health_access() {
    if (is_admin()) {
        $screen = get_current_screen();
        if ('site-health' == $screen - > id) {
            wp_redirect(admin_url());
            exit;
        }
    }
}
add_action('current_screen', 'bdev_block_site_health_access');
