function woo_remove_all_quantity_fields( $return, $product ) {
    switch ( $product->product_type ) :
        case "variable":
            return true;
            break;
        case "grouped":
            return true;
            break;
    endswitch;
}

add_filter( 'woocommerce_is_sold_individually', 'woo_remove_all_quantity_fields', 10, 2 );
